import { useState } from 'react';

const Form = ({ addTodo }) => {
  const [val, setVal] = useState('');
  const add = val => {
    addTodo(val);
    setVal('');
  };
  return (
    <>
      <form>
        <input type='text' value={val} onChange={e => setVal(e.target.value)} />
        <button onClick={() => add(val)}>追加</button>
      </form>
    </>
  );
};

export default Form;
