import { useState } from 'react';

const Example = () => {
  const [fruit, setFruit] = useState('');
  const onChange = e => {
    const newFruit = e.target.value;
    setFruit(prevFruit => (prevFruit === newFruit ? '' : newFruit));
    e.target.checked = prevFruit === newFruit; // 같은 버튼을 클릭한 경우 체크를 해제
  };
  return (
    <>
      <label id='apple'>
        <input
          id='apple'
          type='radio'
          value='Apple'
          checked={fruit === 'Apple'}
          onChange={onChange}
        />
        Apple
      </label>
      <label htmlFor='banana'>
        <input
          id='banana'
          type='radio'
          value='Banana'
          checked={fruit === 'Banana'}
          onChange={onChange}
        />
        Banana
      </label>
      <label htmlFor='cherry'>
        <input
          id='cherry'
          type='radio'
          value='Cherry'
          checked={fruit === 'Cherry'}
          onChange={onChange}
        />
        Cherry
      </label>
      {fruit !== '' ? <h3>私は{fruit}が食べたい</h3> : null}
    </>
  );
};

export default Example;
