import { useState } from 'react';

const Example = () => {
  const [isChecked, setIsChecked] = useState(false);
  const check = () => {
    setIsChecked(!isChecked);
  };
  return (
    <>
      <label>
        チェック:
        <input type='checkbox' checked={isChecked} onChange={check} />
      </label>
      <div>{isChecked ? 'ON!' : 'OFF!'}</div>
    </>
  );
};

export default Example;
