import {
  VStack,
  StackDivider,
  HStack,
  IconButton,
  Text,
} from '@chakra-ui/react';
import { VscCheck } from 'react-icons/vsc';

const List = ({ todos, deleteTodo }) => {
  const complete = id => {
    deleteTodo(id);
  };
  return (
    <VStack
      divider={<StackDivider />}
      //   color={{
      //     sm: 'red.600',
      //     md: 'blue.600',
      //     lg: 'green.600',
      //     xl: 'red.600',
      //   }}
      borderColor='black.100'
      borderWidth='1px'
      borderRadius='3px'
      p={5}
      alignItems='start'
      backgroundColor='white'
    >
      {todos.map(todo => {
        return (
          <HStack key={todo.id} spacing='5'>
            <IconButton
              onClick={() => complete(todo.id)}
              icon={<VscCheck />}
              width='100%'
              isRound
              bgColor='cyan.100'
            >
              完了
            </IconButton>
            <Text>{todo.content}</Text>
          </HStack>
        );
      })}
    </VStack>
  );
};

export default List;
