import { createContext } from 'react';

const CounterCountext = createContext();
const CounterDispatchCountext = createContext();

const CounterProvider = () => {
  const [state, dispatch] = useReducer((prev, { type, step }) => {
    switch (type) {
      case '+':
        return prev + step;
      case '-':
        return prev - step;
      default:
        throw new Error('不明なactionです。');
    }
  }, 0);
  return <CounterCountext.Provider value={state}></CounterCountext.Provider>;
};
