const Calc = () => {
  return (
    /* 完成系のJSX */
    // <CalcProvider>
    //   <Input name="a"/>
    //   <Input name="b" />
    //   <Select />
    //   <Result />
    // </CalcProvider>
    <>
      <div>
        a:
        <input
          type='number'
          name='a'
          value={state.a}
          onChange={numChangeHandler}
        />
      </div>
      <div>
        b:
        <input
          type='number'
          name='b'
          value={state.b}
          onChange={numChangeHandler}
        />
      </div>
      <select value={state.type} name='type' onChange={calculate}>
        {CALC_OPTIONS.map(type => (
          <option key={type} value={type}>
            {type}
          </option>
        ))}
      </select>
      <h3>結果：{state.result}</h3>
    </>
  );
};
