import { useReducer, createContext, useContext } from 'react';

const CalcContext = createContext();
const CalcDispatchContext = createContext();

const CalcProvider = ({ Children }) => {
  const initState = {
    a: 1,
    b: 2,
    result: 3,
  };

  // const [state, dispatch] = useReducer(reducer, initState);

  const [state, dispatch] = useReducer((prev, { type, payload }) => {
    switch (type) {
      case 'change': {
        const { name, value } = payload;
        return { ...prev, [name]: value };
      }
      case 'add': {
        return { ...prev, result: prev.a + prev.b };
      }
      case 'minus': {
        return { ...prev, result: prev.a - prev.b };
      }
      case 'divide': {
        return { ...prev, result: prev.a / prev.b };
      }
      case 'multiply': {
        return { ...prev, result: prev.a * prev.b };
      }
      default:
        throw new Error('operator is invalid');
    }
  }, 0);

  return (
    <CalcContext.Provider value={state}>
      <CalcDispatchContext.Provider value={dispatch}>
        {Children}
      </CalcDispatchContext.Provider>
    </CalcContext.Provider>
  );
};

const useCalc = () => {
  useContext(CalcContext);
};

const useCalcDispatch = () => {
  useContext(CalcDispatchContext);
};

export { CalcProvider, useCalc, useCalcDispatch };
