import { useEffect, useState } from 'react';

const Example = () => {
  const [isDisp, setIsDisp] = useState(true);
  return (
    <>
      {isDisp && <Timer />}
      <button
        onClick={() => {
          setIsDisp(prev => !prev);
        }}
      >
        トグル
      </button>
    </>
  );
};

const Timer = () => {
  const [time, setTime] = useState(0);

  useEffect(() => {
    console.log('init');
    window.setInterval(() => {
      setTime(prev => prev + 1);
    }, 1000);
  }, []);

  useEffect(() => {
    document.title = 'counter:' + time;
    window.localStorage.setItem('time-key-end', time);
  }, [time]);

  return (
    <h3>
      <time>{time}</time>
      <span>秒経過</span>
    </h3>
  );
};

export default Example;
